    and CERT_CODE = #{certCode,jdbcType=VARCHAR}


    and APPOINT_DATE = #{appointDate,jdbcType=DATE}


    and APPINT_SEQ = #{appintSeq,jdbcType=VARCHAR}


    and APPINT_TYPE = #{appintType,jdbcType=VARCHAR}


    and APPLY_SEQ = #{applySeq,jdbcType=VARCHAR}


