mapper.select:

select

  distinct


from prj_task




  order by ${orderByClause}


  limit #{start} , #{pageSize}


select

from prj_task where 


    and ID = #{id,jdbcType=BIGINT}


    and CREAT_TIME = #{creatTime,jdbcType=TIMESTAMP}


    and CREATOR = #{creator,jdbcType=VARCHAR}


    and UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}


    and UPDATOR = #{updator,jdbcType=VARCHAR}


    and IS_DELETE = #{isDelete,jdbcType=CHAR}


    and PRJ_ID = #{prjId,jdbcType=BIGINT}


    and TASK_ID = #{taskId,jdbcType=BIGINT}


    and PRJ_STAGE_INST_ID = #{prjStageInstId,jdbcType=BIGINT}


    and TASK_STATUS = #{taskStatus,jdbcType=VARCHAR}


    and TASK_START_TIME = #{taskStartTime,jdbcType=TIMESTAMP}


    and TASK_END_TIME = #{taskEndTime,jdbcType=TIMESTAMP}


    and TASK_DURATION = #{taskDuration,jdbcType=INTEGER}


    and PAUSE_NUM = #{pauseNum,jdbcType=INTEGER}


    and PAUSE_NUM_MAT = #{pauseNumMat,jdbcType=INTEGER}


    and TASK_PAUSE_DURATION = #{taskPauseDuration,jdbcType=INTEGER}


    and INIT_USER = #{initUser,jdbcType=VARCHAR}


    and CURR_USER = #{currUser,jdbcType=VARCHAR}


    and AUDIT_ATTACH_ADDR = #{auditAttachAddr,jdbcType=VARCHAR}


    and AUDIT_ATTACH_CODE_ADDR = #{auditAttachCodeAddr,jdbcType=VARCHAR}


    and AUDIT_ATTACH_CODE_NAME = #{auditAttachCodeName,jdbcType=VARCHAR}


    and AUDIT_DESC = #{auditDesc,jdbcType=VARCHAR}


    and CERT_DEPT = #{certDept,jdbcType=VARCHAR}


    and CERT_DATE = #{certDate,jdbcType=DATE}


    and CERT_CODE = #{certCode,jdbcType=VARCHAR}


    and CERT_TITLE = #{certTitle,jdbcType=VARCHAR}


    and IS_WITH_CERT = #{isWithCert,jdbcType=CHAR}


    and IS_FETCHED = #{isFetched,jdbcType=CHAR}


    and FETCH_MAN = #{fetchMan,jdbcType=VARCHAR}


    and FETCH_CONTACT_INFO = #{fetchContactInfo,jdbcType=VARCHAR}


    and FETCH_TIME = #{fetchTime,jdbcType=TIMESTAMP}


    and FETCH_DESC = #{fetchDesc,jdbcType=VARCHAR}


    and DEPT_ID = #{deptId,jdbcType=VARCHAR}


    and TASK_REAL_ENDTIME = #{taskRealEndtime,jdbcType=TIMESTAMP}


    and TASK_DUE_DURATION = #{taskDueDuration,jdbcType=INTEGER}


    and TIME_TYPE = #{timeType,jdbcType=VARCHAR}


    and AUDIT_ATTACH_NAME = #{auditAttachName,jdbcType=VARCHAR}


    and ACCEPT_ID = #{acceptId,jdbcType=BIGINT}


    and PS_COUNT = #{psCount,jdbcType=INTEGER}


