select

from prj_task_pause_detail where 


    and ID = #{id,jdbcType=BIGINT}


    and PRJ_TASK_INST_ID = #{prjTaskInstId,jdbcType=BIGINT}


    and PAUSE_ID = #{pauseId,jdbcType=BIGINT}


    and DURATION = #{duration,jdbcType=INTEGER}


    and TIME_TYPE = #{timeType,jdbcType=VARCHAR}


    and PAUSE_DESC = #{pauseDesc,jdbcType=VARCHAR}


    and PAUSE_START_TIME = #{pauseStartTime,jdbcType=TIMESTAMP}


    and PAUSE_OVERTIME = #{pauseOvertime,jdbcType=TIMESTAMP}


    and PAUSE_END_TIME = #{pauseEndTime,jdbcType=TIMESTAMP}


    and PAUSE_DURATION = #{pauseDuration,jdbcType=INTEGER}


    and CREAT_TIME = #{creatTime,jdbcType=TIMESTAMP}


    and IS_SEND_MESSAGE = #{isSendMessage,jdbcType=CHAR}


    and PROVIDE_END_TIME = #{provideEndTime,jdbcType=TIMESTAMP}


    and CREATOR = #{creator,jdbcType=VARCHAR}


    and UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}


    and IS_DELETE = #{isDelete,jdbcType=CHAR}


    and pause_type = #{pauseType,jdbcType=VARCHAR}


    and UPDATOR = #{updator,jdbcType=BINARY}


